mapper.select:
select DISTINCT(hotelName),countryid,t.valueName alias,address,photograph,hotelType,score,MIN(roomPrice) minimum,s.id,s.brandId from hotel s,hotelbrand h,dictionaries t,citys c,houses ho where s.hotelType=t.valueId and c.id=h.citysid and s.countryid=c.id and t.typeName="酒店类型" and ho.hotelId=s.id
and h.id=#{brand} and s.brandId=#{brand}
and s.hotelType=#{rating}
and countryid=#{cid}
and hotelName LIKE CONCAT('%',#{keyword},'%')
and roomPrice BETWEEN #{price1} and #{price2} GROUP BY hotelName
ORDER BY minimum

select name from
citys where
id=#{cid}

select id,brandName,citysid
from hotelbrand
where citysid=#{citysid}

select
id,typeName,valueId,valueName from dictionaries
where typeName=#{type}

select count(*) from dictionaries where
typeName=#{typeName} and valueName
=#{valueName}
